// These are all the static file formats that shakapacker comes preconfigured with support for:
// https://github.com/shakacode/shakapacker/blob/91f7fbec861e88acbe60e0da671cbb0d8665bd0a/package/rules/file.js
declare module "*.bmp" {
  const path: string
  export default path
}

declare module "*.gif" {
  const path: string
  export default path
}

